package leetcode.simple.string;

/**
 * @description: 459. 重复的子字符串
 * @link: <a>https://leetcode-cn.com/problems/repeated-substring-pattern/</a>
 * @author: guoping wang
 * @date: 2018/9/27 16:15
 * @project: cc-leetcode
 */
public class RepeatedSubstringPattern {

    public static void main(String[] args) {
        System.out.println(new RepeatedSubstringPattern().repeatedSubstringPatternOot(""));
        System.out.println(new RepeatedSubstringPattern().repeatedSubstringPattern(""));
    }

    /**
     * 个人常规解法：寻找与第一个字符串一致的，尝试能否进行替换为空
     * 170ms 20.79%
     * @param s
     * @return
     */
    public boolean repeatedSubstringPatternOot(String s) {

        int index = 0;
        for (int i = 1; i <= s.length() / 2; i++) {
            if (s.charAt(i) == s.charAt(0)) {
                // 是否能够整除
                if (s.length() % (index + 1) == 0) {
                    String subStr = s.substring(0, index + 1);
                    String sCopy = new String(s);
                    if (sCopy.replace(subStr, "").equals("")) {
                        return true;
                    }
                }
            }
            index++;
        }
        return false;
    }

    /**
     * 个人常规解法：思路和上面方法一致，只不过不再替换，仅仅比较
     * 27ms 85.11%
     * @param s
     * @return
     */
    public boolean repeatedSubstringPattern(String s) {
        char[] chars = s.toCharArray();

        int index = 0;
        for (int i = 1; i <= chars.length / 2; i++) {
            // 找到相等的进行遍历
            if (chars[i] == chars[0]) {

                // 先判断是否能够整除
                if (chars.length % (index + 1) != 0) {
                    index++;
                    continue;
                }

                // 判断是否都相等------>还可以用substring
                boolean stop = false;
                for (int j = 0; j <= index; j++) {
                    for (int k = 1; k < chars.length / (index + 1); k++) {
                        if (chars[j] != chars[k * (index + 1) +j]) {
                            stop = true;
                            break;
                        }
                    }
                    if (stop) {
                        break;
                    }
                }
                if (!stop) {
                    return true;
                }
            }
            index++;
        }
        return false;
    }
}
